'use client';

import React, { useRef, useState } from 'react';

import { Button } from '@/components/ui/button';
import { Card, CardContent, CardHeader, CardTitle } from '@/components/ui/card';

import { useClickOutside } from '@/hooks/h3-use/use-click-outside';

export default function UseClickOutsideDemo() {
  const cardRef = useRef<HTMLDivElement>(null);
  const [clickedOutside, setClickedOutside] = useState(false);

  useClickOutside(cardRef, () => {
    setClickedOutside(true);
  });

  return (
    <div className="min-h-screen flex items-center justify-center bg-gray-100">
      <Card ref={cardRef} className="w-full max-w-sm shadow-lg">
        <CardHeader>
          <CardTitle>useClickOutside Demo</CardTitle>
        </CardHeader>
        <CardContent>
          <p className="mb-4">Clique fora deste card para disparar uma ação.</p>
          {clickedOutside ? (
            <div className="text-red-500 font-semibold mb-2">
              Você clicou fora do card!
            </div>
          ) : null}
          <Button onClick={() => setClickedOutside(false)} variant="outline">
            Resetar
          </Button>
        </CardContent>
      </Card>
    </div>
  );
}
