'use client';

import React from 'react';

import { Badge } from '@/components/ui/badge';
import { Button } from '@/components/ui/button';
import {
  Card,
  CardContent,
  CardDescription,
  CardHeader,
  CardTitle,
} from '@/components/ui/card';

import { cn } from '@/lib/utils';

import { useIdle } from '@/hooks/h3-use/use-idle';

export default function UseIdleDemo() {
  // 5 seconds of timeout
  const [idle, resetIdle] = useIdle(5000);

  return (
    <Card style={{ padding: 32 }}>
      <CardHeader>
        <CardTitle>useIdle</CardTitle>
        <CardDescription>
          This component uses the <code>useIdle</code> hook to detect if the
          user is idle.
        </CardDescription>
      </CardHeader>
      <CardContent className="space-y-4">
        <Badge
          className={cn(
            'w-fit',
            idle
              ? 'bg-red-500/30 border-red-500'
              : 'bg-green-500/30 border-green-500',
          )}
        >
          The user is{' '}
          <span
            className={cn(
              'font-bold',
              idle ? 'text-red-500' : 'text-green-500',
            )}
          >
            {idle ? 'idle' : 'active'}
          </span>
        </Badge>
        <p>
          Interact with the page (keyboard, mouse, scroll, etc) to reset the
          idle timer.
        </p>
        <Button onClick={resetIdle}>Or click here</Button>
      </CardContent>
    </Card>
  );
}
