'use client';

import React from 'react';

import { useReactHookForm } from '@/hooks/guarahooks/use-react-hook-form';
import { Label } from '@/components/ui/label';
import { Input } from '@/components/ui/input';
import { Button } from '@/components/ui/button';
import {
  Card,
  CardContent,
  CardDescription,
  CardFooter,
  CardHeader,
  CardTitle,
} from '@/components/ui/card';

export default function UseReactHookFormDemo() {
  const {
    register,
    onSubmit,
    formState: { errors },
  } = useReactHookForm<{ firstName: string }>({
    defaultValues: { firstName: '' },
  });

  const handleFormSubmit = (data: { firstName: string }) => {
    console.log('Form Data:', data);
    alert(`Hello, ${data.firstName}!`);
  };

  return (
    <Card className="max-w-md w-full">
      <CardHeader>
        <CardTitle>useReactHookForm</CardTitle>
        <CardDescription>Simplified React Hook Form usage</CardDescription>
      </CardHeader>
      <CardContent className="space-y-4">
        <div className="grid gap-2">
          <Label htmlFor="firstName">First Name</Label>
          <Input
            id="firstName"
            {...register('firstName', { required: 'First name is required' })}
          />
          {errors.firstName && (
            <p className="text-destructive">{errors.firstName.message}</p>
          )}
        </div>
      </CardContent>
      <CardFooter>
        <Button onClick={onSubmit(handleFormSubmit)} className="w-full">
          Submit
        </Button>
      </CardFooter>
    </Card>
  );
}
